(function(){describe("Animated",function(){beforeEach(function(){var e;e=new Animated,e.init()}),afterEach(function(){$("#animated-styles").remove()}),it("should add a style tag to the head tag",function(){expect($("#animated-styles")).toBeInDOM(),expect($("#animated-styles")).toEqual("style")}),it("should find all elements with the data animated property",function(){var e;e=animated.getElements().length,expect(e).toEqual(3)}),it("should set the list of found elements to a property on init",function(){expect(animated.elements.length).toEqual(3)}),it("should return true on canAnimate when all data tags are present",function(){expect(animated.canAnimate(animated.elements.eq(0))).toBe(!0)}),it("should return false on canAnimate when data tags are missing",function(){expect(animated.canAnimate(animated.elements.eq(1))).toBe(!1)}),it("should write keyframe tags for valid elements",function(){expect($("#animated-styles")).toContainText("@keyframes opacity { from { 0; } to { 1; } }")})})}).call(this);